import { useState } from "react";

function Logo() {
  return <h1>My Travel List</h1>;
};

function Form( {handleAddItems} ) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  const handleDescription = (e) => {
    // Set the description...
    setDescription(e.target.value);
  };

  const handleQuantity = (e) => {
    // Set the quantity...
    setQuantity(e.target.value);
  };

  function handleSubmit(e) {
    // Prevent the page from reloading...
    e.preventDefault();
    // Empty description is not allowed...
    if (!description) return;
    // Create a new item...
    const newItem = {id: Date.now(), description: description, quantity: quantity, packed: false};
    // Add new item to the list...
    handleAddItems(newItem);

    setDescription("");
    setQuantity("");
  };

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need to pack?</h3>
      <select value={quantity} onChange={handleQuantity}>
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
      </select>
      <input 
      type="text" 
      placeholder="Item..." 
      value={description} 
      onChange={handleDescription}  
      />
      <button type="submit">Add</button>
    </form>
  );
};

function Item(props) {
  return (
    <li 
    className={props.packed ? "strike-through" : ""}>
    {props.description} | {props.quantity}
    <input type="checkbox"></input>
    </li>
  );
};

function PackingList( {items} ) {
  return (
    <div className="list">
      <ul>
        {items.map((item) => (
          <Item 
          key={item.id}
          description={item.description}
          quantity={item.quantity}
          packed={item.packed}
          />
        ))}
      </ul>
    </div>
  );
};

function Stats() {
  return (
    <footer className="stats">
      <em>You have X items in the list. You already packed Y (Z%).</em>
    </footer>
  );
};

function App() {
  // Responsible for supplying the data to the PackingList Component...
  const [items, setItems] = useState([]);
  const [check, setCheck] = useState(false);

  const handleAddItems = (item) => {
    // Create a new array that has the selected item...
    setItems((prevItem) => 
    [...prevItem, item]
    );
  };

  return (
    <div className="app">
      <Logo />
      <Form handleAddItems={handleAddItems} />
      <PackingList items={items} />
      <Stats />
    </div>
  );
};

export default App;
