import { useState } from "react";

// Initial packing items
const initialItems = [
  { id: 1, description: "Shirt", quantity: 5, packed: false },
  { id: 2, description: "Pants", quantity: 2, packed: true },
];

function Logo() {
  return <h1>My Travel List</h1>;
};

function Form() {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [items, setItems] = useState([]);

  const handleDescription = (e) => {
    // Set the description...
    setDescription(e.target.value);
  };

  const handleQuantity = (e) => {
    // Set the quantity...
    setQuantity(e.target.value);
  };

  const handleAddItems = (item) => {
    // Create a new array that has the selected item...
    setItems((prevItem) => {
      return [...prevItem, item];
    });
  };

  function handleSubmit(e) {
    // Prevent the page from reloading...
    e.preventDefault();
    // Empty description is not allowed...
    if (!description) return;
    // Create a new item...
    const newItem = {id: Date().now(), description: description, quantity: quantity, packed: false};
    // Add new item to the list...
    handleAddItems(newItem);
  };

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need to pack?</h3>
      <select value={quantity} onChange={handleQuantity}>
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
      </select>
      <input 
      type="text" 
      placeholder="Item..." 
      value={description} 
      onChange={handleDescription}  
      />
      <button type="button">Add</button>
    </form>
  );
};

function Item(props) {
  return (
    <li className={props.packed ? "strike-through" : ""}>{props.description} | {props.quantity}</li>
  );
};

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item 
          description={item.description}
          quantity={item.quantity}
          packed={item.packed}
          />
        ))}
      </ul>
    </div>
  );
};

function Stats() {
  return (
    <footer className="stats">
      <em>You have X items in the list. You already packed Y (Z%).</em>
    </footer>
  );
};

function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
};

export default App;
